// Extract Rate Limit Headers
const headers = items[0].json.headers;

// Headers usually look like "17,321" for usage and "100,1000" for limits
// The order is usually 15-minute, Daily

const usageHeader = headers['x-readratelimit-usage'] || '0,0';
const limitHeader = headers['x-readratelimit-limit'] || '0,0';

const [usage15Min, usageDaily] = usageHeader.split(',').map(Number);
const [limit15Min, limitDaily] = limitHeader.split(',').map(Number);

return [
  {
    json: {
      usage_15_min: usage15Min || 0,
      usage_daily: usageDaily || 0,
      limit_15_min: limit15Min || 0,
      limit_daily: limitDaily || 0,
      percentage_15_min: ((usage15Min || 0) / (limit15Min || 1) * 100).toFixed(2) + '%',
      percentage_daily: ((usageDaily || 0) / (limitDaily || 1) * 100).toFixed(2) + '%',
      timestamp: new Date().toISOString()
    }
  }
];
